constexpr s32 FindFloor(const std::string_view input) {
    s32 result = 0;
    for(const auto c : input) {
        if(c == '(') {
            result++;
        } else {
            result--;
        }
    }
    return result;
}


static_assert(FindFloor("") == 0);
static_assert(FindFloor("(())") == 0);
static_assert(FindFloor("()()") == 0);
static_assert(FindFloor("(((") == 3);
static_assert(FindFloor("(()(()(") == 3);
static_assert(FindFloor("())") == -1);
static_assert(FindFloor("))(") == -1);
static_assert(FindFloor(")))") == -3);
static_assert(FindFloor(")())())") == -3);
static_assert(FindFloor(RealInput) == 232);

constexpr s32 FindBasementStep(const std::string_view input) {
    s32 result = 0;
    s32 currentFloor = 0;
    for(const auto c : input) {
        result++;

        if(c == '(') {
            currentFloor++;
        } else {
            currentFloor--;
            if(currentFloor < 0) {
                return result;
            }
        }
    }

    return -1;
}

static_assert(FindBasementStep(")") == 1);
static_assert(FindBasementStep("()())") == 5);
static_assert(FindBasementStep(RealInput) == 1783);